import React, { useState, useEffect } from 'react';
import axios from 'axios';

const SearchPage = () => {
  // State to store the books data and any error messages
  const [books, setBooks] = useState([]);
  const [error, setError] = useState(null);

  // useEffect hook to fetch data from the Google Books API when the component mounts
  useEffect(() => {
    const fetchBooks = async () => {
      try {
        // Retrieve the API key from the environment variables
        const apiKey = process.env.REACT_APP_GOOGLE_BOOKS_API_KEY;

        // Make the API call to fetch books (using "harry potter" as a sample search query)
        const response = await axios.get(
          `https://www.googleapis.com/books/v1/volumes?q=harry+potter&key=${apiKey}`
        );

        // Set the books state with the data from the API
        setBooks(response.data.items);
      } catch (err) {
        // Handle errors and set the error state
        console.error('API call failed:', err);
        setError('Failed to fetch books');
      }
    };

    // Fetch the books when the component is first rendered
    fetchBooks();
  }, []);

  // Render an error message if there's an error
  if (error) return <div className="error-message">{error}</div>;

  return (
    <div className="container">
      <h1>Search Page</h1>
      {/* Check if books are available and display them, or show a "No books found" message */}
      {books.length > 0 ? (
        books.map((book) => (
          <div key={book.id} className="book-card">
            <h2>{book.volumeInfo.title}</h2>
            <p>{book.volumeInfo.authors?.join(', ')}</p>
            {book.volumeInfo.imageLinks?.thumbnail && (
              <img
                src={book.volumeInfo.imageLinks.thumbnail}
                alt={book.volumeInfo.title}
                className="book-thumbnail"
              />
            )}
          </div>
        ))
      ) : (
        <p>No books found</p>
      )}
    </div>
  );
};

export default SearchPage;
