import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import SearchPage from './pages/SearchPage';
import FavoritesPage from './pages/FavoritesPage';
import DoneReadingPage from './pages/DoneReadingPage';

function App() {
  const [favorites, setFavorites] = useState([]);
  const [doneReading, setDoneReading] = useState([]);

  const addToFavorites = (book) => {
    setFavorites((prevFavorites) => [...prevFavorites, book]);
  };

  const addToDoneReading = (book) => {
    setDoneReading((prevDoneReading) => [...prevDoneReading, book]);
  };

  return (
    <Router>
      <div className="app-container">
        <Routes>
          <Route
            path="/"
            element={<SearchPage addToFavorites={addToFavorites} addToDoneReading={addToDoneReading} />}
          />
          <Route path="/favorites" element={<FavoritesPage favorites={favorites} />} />
          <Route path="/done-reading" element={<DoneReadingPage doneReading={doneReading} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
